mapper.update:
UPDATE BOARD
   SET BOARD_TITLE = #{boardTitle},
   	   BOARD_CONTENT = #{boardContent},
   	   CATEGORY = #{category}
 WHERE BOARD_NO = #{boardNo}
   AND DELETE_DATE = NULL

UPDATE BOARD_IMG
   SET ORIGIN_NAME = #{originName},
   	   CHANGE_NAME = #{changeName},
   	   BOARD_IMG = #{boardImg}
 WHERE BOARD_IMG_NO = #{boardImgNo}
   AND DELETE_IMG = NULL
