mapper.insert:
INSERT INTO MEMBER ( USER_NO, USER_ID, USER_PWD, USER_NAME, USER_ADDRESS
) VALUES ( SEQ_USERNO.NEXTVAL, #{userId}, #{userPwd}, #{userName}, #{userAddress}
)

mapper.update:
	UPDATE MEMBER
    SET DELETE_CHECK = 'Y'
    WHERE USER_ID = #{userId}
    AND USER_PWD = #{userPwd}

mapper.select:
SELECT 
	USER_PWD

FROM MEMBER
WHERE USER_ID = #{userId}
 AND USER_ADDRESS = #{userAddress}
 AND DELETE_CHECK = 'N'
