mapper.insert:
INSERT INTO MEMBER ( USER_NO, USER_ID, USER_PWD, USER_NAME, USER_ADDRESS
) VALUES ( SEQ_USERNO.NEXTVAL, #{userId}, #{userPwd}, #{userName}, #{userAddress}
)

mapper.update:
	UPDATE MEMBER
    SET DELETE_CHECK = 'Y'
    WHERE USER_ID = #{userId}
    AND USER_PWD = #{userPwd}

  		UPDATE PROFILE_IMG
  		   SET ORIGIN_NAME = #{originName},
		   	   CHANGE_NAME = #{changeName},
		   	   PROFILE_URL = #{profileUrl}
		 WHERE PROFILE_IMG_NO = #{profileImgNo}
		   AND DELETE_IMG IS NULL
